    parentName: "pre",
    "className": "language-javascript"
  }, "// \u987A\u5E8F\u5408\u5E76\u4E24\u4E2A\u5C0F\u6570\u7EC4left\u3001right \u5230 result\nfunction merge(left, right) {\n  let result = [],\n    ileft = 0,\n    iright = 0\n  while (ileft < left.length && iright < right.length) {\n    if (left[ileft] < right[iright]) {\n      result.push(left[ileft++])\n    } else {\n      result.push(right[iright++])\n    }\n  }\n  while (ileft < left.length) {\n    result.push(left[ileft++])\n  }\n  while (iright < right.length) {\n    result.push(right[iright++])\n  }\n  return result\n}\n\n// \u63D2\u5165\u6392\u5E8F\nfunction insertionSort(arr) {\n  let n = arr.length\n  let preIndex, current\n  for (let i = 1; i < n; i++) {\n    preIndex = i - 1\n    current = arr[i]\n    while (preIndex >= 0 && arr[preIndex] > current) {\n      arr[preIndex + 1] = arr[preIndex]\n      preIndex--\n    }\n    arr[preIndex + 1] = current\n  }\n  return arr\n}\n\n// timsort\nfunction timsort(arr) {\n  // \u7A7A\u6570\u7EC4\u6216\u6570\u7EC4\u957F\u5EA6\u5C0F\u4E8E 2\uFF0C\u76F4\u63A5\u8FD4\u56DE\n  if (!arr || arr.length < 2) return arr\n  let runs = [],\n    sortedRuns = [],\n    newRun = [arr[0]],\n    length = arr.length\n  // \u5212\u5206 run \u533A\uFF0C\u5E76\u5B58\u50A8\u5230 runs \u4E2D\uFF0C\u8FD9\u91CC\u7B80\u5355\u7684\u6309\u7167\u5347\u5E8F\u5212\u5206\uFF0C\u6CA1\u6709\u8003\u8651\u964D\u5E8F\u7684run\n  for (let i = 1; i < length; i++) {\n    if (arr[i] < arr[i - 1]) {\n      runs.push(newRun)\n      newRun = [arr[i]]\n    } else {\n      newRun.push(arr[i])\n    }\n    if (length - 1 === i) {\n      runs.push(newRun)\n      break\n    }\n  }\n  // \u7531\u4E8E\u4EC5\u4EC5\u662F\u5347\u5E8F\u7684run\uFF0C\u6CA1\u6709\u6D89\u53CA\u5230run\u7684\u6269\u5145\u548C\u964D\u5E8F\u7684run\uFF0C\u56E0\u6B64\uFF0C\u5176\u5B9E\u8FD9\u91CC\u6CA1\u6709\u5FC5\u8981\u4F7F\u7528 insertionSort \u6765\u8FDB\u884C run \u81EA\u8EAB\u7684\u6392\u5E8F\n  for (let run of runs) {\n    insertionSort(run)\n  }\n  // \u5408\u5E76 runs\n  sortedRuns = []\n  for (let run of runs) {\n    sortedRuns = merge(sortedRuns, run)\n  }\n  return sortedRuns\n}\n\n// \u6D4B\u8BD5\nvar numbers = [4, 2, 5, 1, 3]\ntimsort(numbers)\n// [1, 2, 3, 4, 5]\n")), mdx("p", null, "\u7531\u4E8E\u7BC7\u5E45\u6709\u9650\uFF0C\u5C31\u4E0D\u518D\u8BE6\u7EC6\u5C55\u5F00\uFF0C\u5982\u679C\u5BF9\u5176\u611F\u5174\u8DA3\uFF0C\u53EF\u4EE5\u67E5\u770B", mdx("a", {
