# class ListNode:

#     def __init__(self, x):

#         self.val = x

#         self.next = None

class Solution:
    # 返回合并后列表

    def Merge(self, pHead1, pHead2):
        # write code here

        head = ListNode(-1)
        cur = head
        while pHead1 and pHead2:
            if pHead1.val < pHead2.val:
                tmp = pHead1
                pHead1 = pHead1.next
            else:
                tmp = pHead2
                pHead2 = pHead2.next
            cur.next = tmp
            cur = cur.next
        if pHead1:
            cur.next = pHead1
        else:
            cur.next = pHead2
        return head.next
