class Solution:
    def IsPopOrder(self, pushV, popV):
        # write code here

        st = []
        while len(pushV):
            st.append(pushV[0])
            pushV = pushV[1:]
            while len(popV) and st[-1]==popV[0]:
                st.pop()
                popV = popV[1:]
        if len(popV):
            return False
        return True
