# class TreeNode:

#     def __init__(self, x):

#         self.val = x

#         self.left = None

#         self.right = None

class Solution:
    # 返回从上到下每个节点值列表，例：[1,2,3]

    def PrintFromTopToBottom(self, root):
        # write code here

        res = []
        if not root:
            return res
        q = [root] # queue

        lc = len(q) # layer count

        while len(q):
            node = q[0]
            q = q[1:]
            res.append(node.val)
            lc -= 1
            if node.left:
                q.append(node.left)
            if node.right:
                q.append(node.right)
            if lc == 0:
                lc = len(q)
        return res
