    def NumberOf1Between1AndN_Solution(self, n):
        base = 1
        digit = 0
        pre = post = 0
        cnt = 0
        while base <= n:
            pre = n // base
            post = n % base
            digit = pre % 10
            cnt += (pre+8)/10 * base
            if digit == 1:
                cnt += post + 1
            base *= 10
        return cnt
