    def FirstNotRepeatingChar(self, s):
        # write code here

        cnts = {}
        ans = -1
        for i in range(len(s)):
            if s[i] in cnts:
                cnts[s[i]] = (cnts[s[i]][0], False)
            else:
                cnts[s[i]] = (i, True)
        for key in cnts:
            val = cnts[key]
            if not val[1]:
                continue
            else:
                if ans == -1:
                    ans = val[0]
                else:
                    ans = min(ans, val[0])
        return ans
