    def FindNumbersWithSum(self, arr, target):
        left,right = 0,len(arr)-1
        res = []
        while left < right:
            s = arr[left] + arr[right]
            if s == target:
                res = [arr[left], arr[right]]
                break
            elif s > target:
                right -= 1
            else:
                left += 1
        return res
