    def IsContinuous(self, numbers):
        if len(numbers) != 5:
            return False
        m = {}
        min_card = -1
        max_card = -1
        for x in numbers:
            m[x] = 1 if x not in m else m[x]+1
            if x and m[x] > 1:
                return False
            if min_card == -1 and x:
                min_card = x
            elif x:
                min_card = min(min_card, x)

            if max_card == -1 and x:
                max_card = x
            elif x:
                max_card = max(max_card, x)

        if max_card - min_card > 4:
            return False
        else:
            return True
