    def StrToInt(self, s):
        if not s:
            return 0
        is_neg = False
        if s[0] == '-':
            is_neg = True
            s = s[1:]
        elif s[0] == '+':
            s = s[1:]

        ans = 0
        m = {str(x):x for x in range(10)}
        for c in s:
            if c in m:
                ans = ans*10 + m[c]
            else:
                ans = 0
                break
        return ans if not is_neg else -ans
