    def deleteDuplication(self, pHead):
        cur = pHead
        prev = ListNode(-1)
        prev.next = pHead
        pHead = prev
        del_flag = False
        while cur and cur.next:
            if cur.val == cur.next.val:
                cur.next = cur.next.next
                del_flag = True
            else:
                if del_flag:
                    prev.next = cur.next
                    del_flag = False
                else:
                    prev = cur
                cur = cur.next
        if del_flag:
            prev.next = None
        return pHead.next
