    # 返回二维列表[[1,2],[4,5]]

    def Print(self, pRoot):
        if not pRoot:
            return []
        q = [pRoot]
        res = [[pRoot.val]]
        vec = []
        while q:
            node = q[0]
            q = q[1:]
            if node.left:
                vec.append(node.left)
            if node.right:
                vec.append(node.right)
            if len(q) == 0 and vec:
                res.append([x.val for x in vec])
                q = vec
                vec = []
        return res
