class Solution:
    # array 二维列表

    def Find(self, target, array):
        # write code here

        if not len(array):
            return False
        R,C = len(array),len(array[0])
        if not C:
            return False
        row = R-1
        col = 0
        while row >= 0 and col < C:
            if target > array[row][col]:
                col += 1
            elif target < array[row][col]:
                row -= 1
            else:
                return True
        return False
