    def pop(self):
        # return xx

        if len(self.stack_pop):
            return self.stack_pop.pop()
        # 鲁棒性检验：stack_push不能为空，否则pop失败

        if not len(self.stack_push):
            raise IndexError
        while len(self.stack_push):
            self.stack_pop.append(self.stack_push.pop())
        return self.stack_pop.pop()
