class Solution:
    def minNumberInRotateArray(self, rotateArray):
        # write code here

        if len(rotateArray) == 0: return 0
        rotateArray = rotateArray[::-1]
        res = rotateArray[0]
        for val in rotateArray[1:]:
            if val <= res:
                res = val
            else:
                break
        return res
