# class ListNode:

#     def __init__(self, x):

#         self.val = x

#         self.next = None

class Solution:
    # 返回ListNode

    def ReverseList(self, pHead):
        # write code here

        emptyHead = None
        prevNode = emptyHead

        curNode = pHead
        while curNode:
            tmp = curNode.next
            curNode.next = prevNode
            prevNode = curNode
            curNode = tmp
        return prevNode
