class Solution:
    # matrix类型为二维列表，需要返回列表

    def printMatrix(self, matrix):
        # write code here

        res = []
        row = len(matrix)
        if not row:
            return res
        col = len(matrix[0])
        if not col:
            return res
        cnt = 0
        size = row*col
        margin = 0
        i = j = 0
        while cnt < size:
            # turn right

            while j < col-margin:
                res.append(matrix[i][j])
                cnt += 1
                if cnt == size:
                    return res
                j += 1
            j -= 1
            i += 1
            # turn down

            while i < row-margin:
                res.append(matrix[i][j])
                cnt += 1
                if cnt == size:
                    return res
                i += 1
            i -= 1
            j -= 1
            # turn left

            while j >= margin:
                res.append(matrix[i][j])
                cnt += 1
                if cnt == size:
                    return res
                j -= 1
            j += 1
            i -= 1
            # turn up

            margin += 1
            while i >= margin:
                res.append(matrix[i][j])
                cnt += 1
                if cnt == size:
                    return res
                i -= 1
            i += 1
