#     def __init__(self, x):

#         self.label = x

#         self.next = None

#         self.random = None

class Solution:
    # 返回 RandomListNode

    def Clone(self, pHead):
        if not pHead:
            return None
        cur = pHead
        # 第一次遍历复制值

        while cur:
            tmp = RandomListNode(cur.label)
            tmp.next = cur.next
            cur.next = tmp
            cur = tmp.next
        # 第二次遍历复制random指针

        cur = pHead
        while cur:
            if cur.random:
                cur.next.random = cur.random.next
            cur = cur.next.next
        # 拆分两个链表

        cur = pHead
        ans = nxt = pHead.next
        while cur:
            nxt = cur.next
            if nxt.next:
                cur.next = nxt.next
                nxt.next = nxt.next.next
            else:
                cur.next = None
            cur = cur.next
        return ans
