    def Permutation(self, ss):
        if not ss:
            return []

        if len(ss)==1:
            return [ss]

        else:
            result=[]
            a=ss[-1]
            for ii in self.Permutation(ss[:-1]):
                for j in range(len(ii)+1):
                    result.append(ii[:j]+a+ii[j:])

            result=list(set(result))
            result.sort()
            return result
