    def MoreThanHalfNum_Solution(self, numbers):
        n_cnts = {}
        line = int(len(numbers)/2)
        for n in numbers:
            if n in n_cnts:
                n_cnts[n] += 1
            else:
                n_cnts[n] = 1
            if n_cnts[n] > line:
