class Solution:
    def GetLeastNumbers_Solution(self, tinput, k):
        heap = []
        if len(tinput)==0 or k==0 or k>len(tinput):
            return heap
        # 取反数值，用最小堆接口实现最大堆

        for x in tinput:
            if len(heap) < k:
                heapq.heappush(heap, -x)
            elif -x > heap[0]:
                heapq.heappop(heap)
                heapq.heappush(heap, -x)
        return [-heapq.heappop(heap) for _ in range(len(heap))][::-1]
