    def GetUglyNumber_Solution(self, index):
        # write code here

        if index == 0:
            return 0
        arr = [1]
        p2 = p3 = p5 = 0
        while index > len(arr):
            val2 = arr[p2] * 2
            while val2 <= arr[-1]:
                p2 += 1
                val2 = arr[p2] * 2
            val3 = arr[p3] * 3
            while val3 <= arr[-1]:
                p3 += 1
                val3 = arr[p3] * 3
            val5 = arr[p5] * 5
            while val5 <= arr[-1]:
                p5 += 1
                val5 = arr[p5] * 5
            new_val = min(val2, val3, val5)
            arr.append(new_val)
        return arr[index-1]
