    def __init__(self, val):
        self.val = val
        self.next = None

class Solution:
    def LastRemaining_Solution(self, n, m):
        if n == 0:
            return -1
        head = prev = Node(0)
        cur = None
        for i in range(1,n):
            cur = Node(i)
            prev.next = cur
            prev = cur
        prev.next = head

        while head.next:
            for _ in range(m-1):
                prev = head
                head = head.next
            if head.next == prev:
                prev.next = None
                head = prev
            else:
                prev.next = head.next
                head = head.next

        return head.val
