    def multiply(self, A):
        a = b = 1
        B = [1 for _ in range(len(A))]
        for i in range(1, len(A)):
            a *= A[i-1]
            B[i] = a

        for i in range(len(A)-2, -1, -1):
            b *= A[i+1]
            B[i] *= b
        return B
