        self.p = 0
        self.fsc = ''

    def FirstAppearingOnce(self):
        while self.p < len(self.s):
            c = self.s[self.p]
            if c not in self.m:
                self.m[c] = self.p
                if self.fsc in ('#', ''):
                    self.fsc = c
            else:
                self.m[c] = -1
                min_pos = len(self.s)
                if self.fsc == c:
                    self.fsc = '#'
                    for key in self.m:
                        if self.m[key] != -1 and self.m[key] < min_pos:
                            min_pos = self.m[key]
                            self.fsc = key
            self.p += 1

        return self.fsc

    def Insert(self, c):
        # write code here
        self.s += c
