    def EntryNodeOfLoop(self, pHead):
        fast = pHead
        slow = pHead
        while fast and fast.next:
            fast = fast.next.next
            slow = slow.next
            if fast == slow:
                break

        if not fast or not fast.next:
            return None

        circle = pHead
        while circle != fast:
            fast = fast.next
            circle = circle.next
        return circle
