    # 返回对应节点TreeNode

    def __init__(self):
        self.cnt = 0

    def KthNode(self, pRoot, k):
        if not pRoot:
            return None
        ans = self.KthNode(pRoot.left, k)
        if ans:
            return ans
        self.cnt += 1
        if self.cnt == k:
            return pRoot
        return self.KthNode(pRoot.right, k)
